/**
 * @param {number[]} array 
 * @param {number} value
 */
const findLowerIndex = function (array, value) {
  let low = 0;
  let high = array.length - 1;
  let index = 0;
  while (low < high) {
    index = low + (((high - low) / 2) | 0);
    if (array[index] < value) {
      low = index + 1;
    } else {
      high = index;
    }
  }
  if (array[index] > value) {
    index--;
  }
  return index;
};

