   * @param {CubicBezierCurve} curve
   */
  CurvedPath.prototype.add = function (curve) {
    this.curves.push(curve);
    if (this.curvePreviousLengths.length > 0) {
      this.curvePreviousLengths.push(this.getLength() + curve.getLength());
    }
