    firstControlX,
    firstControlY,
    secondControlX,
    secondControlY,
    destinationX,
    destinationY,
    isRelative) {
    const endX = this.getEndX();
    const endY = this.getEndY();
    if (isRelative) {
      firstControlX += endX;
      firstControlY += endY;
